class Solution {
    public boolean isPowerOfThree(int n) {
        double epsilon = 0.0000000001; // to handle precision issues in Java
        if (n == 0) return false;
        double x = Math.log(n) / Math.log(3);
        return Math.abs(x - Math.round(x)) < epsilon;
    }
}
