#include <stdio.h>

unsigned long long factorial_recursive(int n) {
    if (n == 0) {
        return 1;
    } else {
        return n * factorial_recursive(n - 1);
    }
}

unsigned long long factorial_iterative(int n) {
    unsigned long long result = 1;
    for (int i = 1; i <= n; i++) {
        result *= i;
    }
    return result;
}

int main() {
    int number;
    unsigned long long recursive_result, iterative_result;

    printf("Enter a positive integer: ");
    scanf("%d", &number);

    if (number < 0) {
        printf("Invalid input. Please enter a positive integer.\n");
        return 1;
    }

    recursive_result = factorial_recursive(number);
    iterative_result = factorial_iterative(number);

    printf("Factorial (Recursive): %llu\n", recursive_result);
    printf("Factorial (Iterative): %llu\n", iterative_result);

    return 0;
}

